import React, { useState, useEffect } from 'react';
import './Chat.css';
import initialPosts from './posts';

function Chat() {
    const [inputValue, setInputValue] = useState("");
    const [messages, setMessages] = useState(initialPosts);

    // useEffect(() => {
    //     fetch('http://localhost:8080/messages')
    //         .then(response => {
    //             if (response.ok) {
    //                 return response.json();
    //             }
    //             throw new Error('Failed to fetch messages');
    //         })
    //         .then(data => {
    //             setMessages(data);
    //         })
    //         .catch(error => {
    //             console.error(error);
    //         });
    // }, []);

    const handleSubmit = (event) => {
        console.log("handlesubmit");
        event.preventDefault();
        if (!inputValue.trim()) return;
        fetch('http://localhost:8080/messages', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ author: 'Anonymous', text: inputValue })
        })
            .then(response => {
                if (response.ok) {
                    return response.json();
                }
                throw new Error('Failed to add message');
            })
            .then(data => {
                setMessages((prevMessages) => [...prevMessages, data]);
                setInputValue('');
            })
            .catch(error => {
                console.error(error);
            });
    };

    const onButtonClick = () => {
        console.log(inputValue);
        console.log("onbuttonclick");
        // setMessages(inputValue);
        // setInputValue("");
    };

    return (
        <div className="container">
            <div className="chat">
                {messages.map((message, index) => (
                    <div className="message" key={index}>
                        <div className="author">{message.author}</div>
                        <div className="text">{message.text}</div>
                    </div>
                ))}
            </div>
            <form onSubmit={handleSubmit}>
                <div className="input-container">
                    <input
                        type="text"
                        value={inputValue}
                        onChange={(event) => setInputValue(event.target.value)}
                        className="input"
                        placeholder="Type a message"
                    />
                    <button type="submit" className="send-button" onClick={onButtonClick}>
                        Send
                    </button>
                </div>
            </form>
        </div>
    );
}

export default Chat;
